class Solution:
    def waysToReachStair(self, k: int) -> int:

        @cache
        def dfs(currentIndex,wentBack,jumps):
            #print(currentIndex,wentBack,jumps)
            if(currentIndex==k):
                return 1 + dfs(currentIndex+pow(2,jumps),False,jumps+1) + (dfs(currentIndex-1,True,jumps) if (wentBack==False and currentIndex>0) else 0)
            elif(currentIndex>k+1):
                return 0

            temp=dfs(currentIndex+pow(2,jumps),False,jumps+1) + (dfs(currentIndex-1,True,jumps) if (wentBack==False and currentIndex>0) else 0)
            #print(currentIndex,temp)
            return temp

        return dfs(1,False,0)
