public class Solution {
    public int WaysToReachStair(int k) {
        int res = 0;
        int j=0;
        while(j<30){
            int p= 1;
            for(int i=0;i<j;i++){
                p+=(1<<i);
            }

            res+= (int)C((long)j+1,(long) p-k);
            j++;
        }
        return res;
    }
    public long C(long N, long K)
{
    if(K==0) return 1;
    if(K<0) return 0;

   long r = 1;
   long d;
   if (K > N) return 0;
   for (d = 1; d <= K; d++)
   {
      r *= N--;
      r /= d;
   }
   return r;
}
}
