class Solution {
public:

    #define ll long long
    ll ans=0;

    void f(ll k, ll pos, ll power, bool permit)
    {
        if(pos-k>1)
        {
            return;
        }
        if(pos==k)
        {
            ans++;
        }
        if(permit && pos!=0)
        {
            f(k,pos-1,power,0);
        }
        f(k,pos+(1ll<<power),power+1,1);
    }

    int waysToReachStair(int k) {
        if(k<=32)
        {
            f(k,1ll,0ll,1);
            return ans;
        }
        map<ll,vector<ll>> m;
        map<ll,ll> m1;
        m[32]={1,6,15,20,15,6,1};
        m1[26]=1;
        m1[27]=6;
        m1[28]=15;
        m1[29]=20;
        m1[30]=15;
        m1[31]=6;
        m1[32]=1;
        for(ll i=6;i<33;i++)
        {
            m[(1ll<<i)].push_back(1);
            vector<ll> tmp(m[(1ll<<(i-1))]);
            ll ptr=(1ll<<i)-(i+1);
            m1[ptr]=1;
            ptr++;
            ll sz=tmp.size();
            for(ll j=1;j<sz;j++)
            {
                m[(1ll<<i)].push_back(tmp[j]+tmp[j-1]);
                m1[ptr]=(tmp[j]+tmp[j-1]);
                ptr++;
            }
            m[(1ll<<i)].push_back(1);
            m1[ptr]=1;
        }
        return m1[k];
    }
};
