class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def dp(step, jump, fall):
            ans = 0
            if step == k: ans += 1
            if step > k + 1: return 0
            if fall and step: ans += dp(step - 1, jump, 0)
            ans += dp(step + (1 << jump), jump+1, 1)
            return ans
        return dp(1, 0, 1)
