class Solution:
    LOGMAX = 31

    def waysToReachStair(self, k: int) -> int:
        ans = 0
        for j in range(self.LOGMAX):
            diff = (1 << j) - k
            # Uncomment the following line for debug output
            # print(j, (1 << j), diff)
            if diff > j + 1:
                break
            if diff < 0:
                continue
            p = int(diff)
            q = j - p + 1
            if p > q:
                p, q = q, p
            cur = 1
            for i in range(q + 1, p + q + 1):
                cur *= i
            for i in range(2, p + 1):
                cur //= i
            # Uncomment the following lines for debug output
            # print(j, diff)
            # print(p, q)
            # print(cur)
            ans += cur
        return int(ans)
