class Solution:
    def __init__(self):
        self.mp = {}

    def helper(self, k, curr_pos, jump, last):
        if curr_pos > k + 1 or curr_pos < 0:
            return 0
        if last == 1 and (curr_pos - k) == 1:
            return 1

        key = f"{curr_pos} {jump} {last}"
        if key in self.mp:
            return self.mp[key]

        ans = 0
        if curr_pos == k:
            ans += 1
        if last != 0:
            ans += self.helper(k, curr_pos - 1, jump, 0)
        ans += self.helper(k, curr_pos + 2 ** jump, jump + 1, 1)

        self.mp[key] = ans
        return ans

    def waysToReachStair(self, k):
        self.mp.clear()
        return self.helper(k, 1, 0, -1)
