class Solution {
public:

    int numWays(int k, int cur, int jump, bool canMoveDown, map<tuple<int, int, bool>, int>& memo) {
        int ways = 0;   
        if (cur < 0 or cur > k + 1) return 0;    
        if (cur == k) {
            ways++;
        }

        tuple<int, int, bool> key = make_tuple(cur, jump, canMoveDown);


        if (memo.find(key) != memo.end()) {
            return memo[key];
        }


        ways += numWays(k, cur + (1 << jump), jump + 1, true, memo);
        if (canMoveDown and cur > 0) {
            ways += numWays(k, cur - 1, jump, false, memo);
        }

        memo[key] = ways;
        return ways;
    }

    int waysToReachStair(int k) {
        if (k == 0) return 2;
        map<tuple<int, int, bool>, int> memo;
        return numWays(k, 1, 0, true, memo);
    }
};
