class Solution:
    def waysToReachStair(self, k: int) -> int:

        @cache
        def helper(curr, jump, state, k):
            if (state == 0 and curr > k) or (state == 1 and curr - 1 > k):
                return 0
            if curr == k:
                return 1 + helper(curr + (1 << jump), jump + 1, 1, k) + (helper(curr - 1, jump, 0, k) if state != 0 else 0)
            return helper(curr + (1 << jump), jump + 1, 1, k) + (helper(curr - 1, jump, 0, k) if state != 0 else 0)

        return helper(1, 0, -1, k)
