class Solution {
public:

    int solve(int pos,int j, int jump,int k,vector<unordered_map<int,unordered_map<int,int>>> & mp){

        if(pos < 0){return 0;}
        if(pos > k+1){return 0;}
        int p1 = 0, p2 = 0;
        if(mp[j].find(pos) != mp[j].end() && mp[j][pos].find(jump) != mp[j][pos].end()){return mp[j][pos][jump];}
        if(pos == k){p1++;}

        if(j != 0){p1 += solve(pos-1,0,jump,k,mp);}
        p2 += solve(pos+pow(2,jump),1,jump+1,k,mp);

        return mp[j][pos][jump]=p1+p2;
    }



    int waysToReachStair(int k) {
        vector<unordered_map<int,unordered_map<int,int>>> mp(2);
        return solve(1,1,0,k,mp);
    }
};
