class Solution:
    def waysToReachStair(self, k: int) -> int:

        @cache
        def recur(curr,jump,downed):
            ret=0
            if curr>k+1:
                return 0
            if curr==k:
                ret+=1
            if not downed and curr>0:
                ret+=recur(curr-1,jump,True)
            ret+=recur(curr+2**jump,jump+1,False)

            return ret
        return recur(1,0,False)
