class Solution {
public:
    long long nCr(long long int n, long long int k) {
        if (k > n) return 0;
        if (k == 0 || k == n) return 1;

        long long res = 1;
        for (int i = 1; i <= k; ++i) {
            res *= (n - k + i);
            res /= i;
        }
        return res;
    }
    long long int poww(long long int n, long long int k)   { 
    long long int x=1;   
    while (k)   {
        if (k & 1)   
        { x*= n;}
        n*=n;
        k>>=1;
    }
    return x;
}  
    int waysToReachStair(int k) {
        long long ans=0;
        for(int i=0;i<32;i++) {
            int val=poww(2,i)-k;
            if(val>=0)
            ans+=nCr(i+1,val);
        }
        return ans;
    }
};
