class Solution:
    def waysToReachStair(self, k: int) -> int:


        res = 0

        @lru_cache(maxsize=None)
        def rc(curIdx, jump, canUseBackwords):
            if canUseBackwords == False and curIdx > k:
                return 0
            cur_res = 0
            if curIdx == k:
                cur_res += 1
            # operation 1
            if canUseBackwords == True and curIdx > 0 and rc(curIdx-1, jump, False)!=0:
                cur_res += rc(curIdx-1, jump, False)
            # operation 2
            if curIdx<=k+1 and rc(curIdx+2**jump, jump+1, True)!=0:
                cur_res += rc(curIdx+2**jump, jump+1, True)
            return cur_res



        return rc(1, 0, True)
