import math

class Solution:
    def waysToReachStair(self, k: int) -> int:
        count = 0
        end = int(math.log2(k)) if k > 0 else 0
        for j in range(end + 3):
            jr = 1
            if j > 0:
                jr = 2 ** (j)
            d = jr - k
            print(f"{j=} {jr=} {d=}")
            if d < 0 or d > j + 1:
                continue
            else:
                count += math.comb(j+1, d)
        return count
