class Solution {
public:

    using vi = vector<int>;

    map<vi, int> dp;

    int go(int stair, int jumps, int can_prev, int k) {
        if(stair > (k + 1)) return 0;
        if(dp.find({stair, jumps, can_prev}) != dp.end()) return dp[{stair, jumps, can_prev}];
        int ans = 0;
        if(stair == k) ans ++;
        ans += go(stair + (1 << jumps), jumps + 1, 1, k);
        if(can_prev) ans += go(stair - 1, jumps, 0, k);
        return dp[{stair, jumps, can_prev}] = ans;
    }

    int waysToReachStair(int k) {
        return go(1, 0, 1, k);
    }
};
