class Solution {
public:

    int nCr(int n,int r){
        if(r>n) return 0;
        if(r<0) return 0;
        r=min(r,n-r);
        long long ans=1;
        vector<long long> a;
        for(long long i=1,j=n;i<=r;j--,i++){
            a.push_back(j);
        }
        for(long long i=1;i<=r;i++){
            long long p=i;
            for(auto &e:a){
                int g=__gcd(p,e);
                p/=g;
                e/=g;
            }
        }
        for(auto e:a) ans*=e;
        return ans;
    }

    int waysToReachStair(int k) {
        long long p=1;
        int j=0,ans=0;
        while(1){
            long long l=p-j-1,r=p;
            if(k<l) break;
            if(r<k){
                p*=2;
                j++;
                continue;
            }
            int t=p-k;
            ans+=nCr(j+1,t);
            p*=2;
            j++;
        }
        return ans;
    }
};
