class Solution {
    Map<String, Integer> dp;
    private int solve(int k,int t,int jump,boolean flag) {
        if (k>t+1) return 0;
        if (k<0) return 0;
        int ans = 0;
        if (k==t) ans = 1;
        String str = k+"-"+jump+"-"+flag;
        if (dp.containsKey(str)) return dp.get(str);
        if (!flag && k>0) ans += solve(k-1,t,jump,true);
        int ne = k + (1<<jump);
        ans += solve(ne,t,jump+1,false);
        dp.put(str,ans);
        return ans;
    }
    public int waysToReachStair(int k) {
        dp = new HashMap<>();
        return solve(1,k,0,false);
    }
}
