class Solution {
 private:
    unordered_map<int, vector<vector<int>>> dp;
    int prahlad(int i, int k, int j, bool ch) {
        // if ( j>k+k&&i > k + 5)
        // return 0;
        if (i >k +5)
            return 0;

        if(j > 31)
            return 0;
        // if(dp.count(i)&&dp[i][j][c] != -1 && dp[i][j][c] != -1)
        // return dp[i][j][c];
        if(dp.count(i)&&dp[i][j][ch] != -1)
            return dp[i][j][ch];int ans = (i == k);
        if (ch==true)
    ans += prahlad(i - 1,k, j, false);
        if (i + (1 << j) <= k + 1)
            ans += prahlad(i + (1 << j), k, j + 1, true);
        // cout<<dp[i-1];

        dp[i].resize(32, vector<int>(2, -1));
     return dp[i][j][ch] = ans;
    }
public:
    int waysToReachStair(int k) {
        return prahlad(1, k, 0, 1);
    } 
};
