class Solution {
    unordered_map<int, unordered_map<int, unordered_map<bool, int>>> memo;

    int countWays(int k, int current, int resss, bool checkk) {
        if (current >= k + 6)
            return 0;
        if (resss > 31)
            return 0;
        if (memo[current][resss].count(checkk))
            return memo[current][resss][checkk];

        int abc=(current == k);
        int ways = abc;
        if (checkk && current > 0)
            ways += countWays(k, current - 1, resss, false);


         int nextJump = pow(2, resss);
        if (current + nextJump <= k + 1)
            ways += countWays(k, current + nextJump, resss + 1, true);


        return memo[current][resss][checkk] = ways;
    }

public:
    int waysToReachStair(int k) {
        return countWays(k, 1, 0, true);
    }
};
