class Solution:
    def waysToReachStair(self, k: int) -> int:
        @lru_cache(None)
        def dfs(i = 1, jump = 0, jumped = False):
            if i<0:
                return 0
            if i>k+1:
                return 0
            curr = 1 if i==k else 0
            if jumped == False:
                return curr + dfs(i-1, jump, True) + dfs(i+(2**jump), jump+1, False)
            else: # jumped = true
                return curr + dfs(i+(2**jump), jump+1, False)
        return dfs()
