class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def dfs(i, jump, can_go_down):
            if i > k + 1:
                return 0
            res = (i == k) + dfs(i + 2**jump, jump + 1, True)
            if can_go_down:
                res += dfs(i - 1, jump, False)
            return res
        return dfs(1, 0, True)
