class Solution:
    def waysToReachStair(self, k: int) -> int:
        if k<2:
            return 2**(k+1)
        @lru_cache(None)
        def dfs(n, jump, fl):
            if n>k+1:
                return 0
            ans = 0
            if n == k:
                ans+=1
            if not fl and n!=0:
                ans+=dfs(n-1, jump, True)
            ans+=dfs(n+2**jump, jump+1, False)
            return ans
        return dfs(1, 0, False)
