/**
 * @param {number} k
 * @return {number}
 */
var waysToReachStair = function(k) {
    const memo = {}
    const compute = function(pos,jumps,downAvailable){
        if(pos>k+1) return 0;
        const key = `${pos},${jumps},${downAvailable?1:0}`;
        let ans=0;
        if(key in memo) return memo[key];
        if(pos == k)  ans+=1;
        if(downAvailable){
            ans = ans + compute(pos+Math.pow(2,jumps),jumps+1,downAvailable) + compute(pos-1,jumps,!downAvailable);
        }else{
            ans = ans + compute(pos+Math.pow(2,jumps),jumps+1,!downAvailable);
        }
        memo[key] = ans;
        return ans;
    }
        //2 -> 1 
    return compute(1,0,true);
    //console.log(memo);
};
