class Solution {
public:
    int _k;
    using ll = long long;
    map<tuple<ll, ll, ll>, ll> memo; 
    ll solve(bool t, ll i, ll s) {
        if (i > _k + 20) return 0;
        if (memo.count({t, i, s})) return memo[{t, i, s}];
        ll ans = i == _k;
        if (t == 0 && i != 0) ans += solve(true, i - 1, s);
        ans += solve(false, i + s, s * 2);

        memo[{t, i, s}] = ans;
        return ans;
    }
    int waysToReachStair(int k) {
        memo.clear();
        _k = k;
        return solve(0, 1, 1);
    }
};
