class Solution {
public:
    string che(long long i, int c, bool javase) {
        return to_string(i)+"_"+to_string(c)+"_"+to_string(javase);
    }

    long long geti(long long i,int c,bool javase,int k,unordered_map<string,long long> &dp) {
        if (i==k) {
            long long total_ways = 1;
            if (javase && i>0) {
                total_ways+=geti(i-1,c,false,k,dp);
            }
            total_ways+=geti(i+(1<<c),c+1,true,k,dp);
            return total_ways;
        }
        if (i<0) return 0;
        if (i>k+(1<<c)) return 0;

        string s = che(i,c,javase);
        if (dp.find(s)!=dp.end()) {
            return dp[s];
        }

        long long cnt = 0;
        if (javase && i) {
            cnt += geti(i-1,c,false,k,dp);
        }
        cnt+=geti(i+(1<<c),c+1,true,k,dp);

        return dp[s]=cnt;
    }

    long long waysToReachStair(int k) {
        if (k == 0) 
            return 2;

        unordered_map<string, long long> dp;
        return geti(1,0,true,k,dp);
    }
};
