class Solution {
public:
    map<int,map<int,map<bool,int>>>mpp;
    int f(int i,int k,bool prev,int j){

        if(i<0 || i>k+1)
            return 0;
        if(mpp.count(i) && mpp[i].count(j) && mpp[i][j].count(prev))
            return mpp[i][j][prev];

        int ans1=0,ans2=0;

        if(!prev)
            ans1+=f(i-1,k,true,j);
            ans2+=f(i+(1<<j),k,false,j+1);
        if(i==k)
        return   mpp[i][j][prev]=1+ans1+ans2;
        return  mpp[i][j][prev]=ans1+ans2;

    }
    int waysToReachStair(int k) {
        return f(1,k,false,0);
    }
};
