#define ll long long

map<pair<ll,pair<bool,ll>>,ll> mp;

class Solution {
public:

    ll rec(ll i, ll jump , bool last ,ll k){
        ll diff=i-k;
        if(diff>1) return 0LL;
        if(mp.find({i,{last,jump}})!=mp.end()) return mp[{i,{last,jump}}];
        ll ans=0;
        if(i==k)
            ans+=1LL;
        if(i-1>=0&&last){
            ans+=rec(i-1,jump,false,k);
        }
        ll vk=1<<jump;
        ans+=rec(i+vk,jump+1,true,k);
        return mp[{i,{last,jump}}]=ans;
    }

    int waysToReachStair(int k) {
        mp.clear();
        if(k==0) return 2;
        return rec(1,0,true,k);
    }
};
