class Solution:
    def waysToReachStair(self, k: int) -> int:
        if k>100:
            j =1
            while 2**j < k: 
                j+=1
            dt = 2**j - k
            if dt > j+1 :
                return 0
            return math.comb(j+1,dt)


        co = Counter()
        def dfs(now,inc,canDown):
            if now > 1000:
                return
            co[now] += 1
            # co[now-1] += 1
            dfs(now+inc,inc*2,True)
            if canDown:
                dfs(now-1,inc,False)

        dfs(1,1,True)

        if k <= 100:
            return co[k]
        # print(len(co))
        return 0
