class Solution {
    unordered_map<string, int> dp;
public:
    int waysToReachStair(int k) {
        return fn(1, 0, 0, k);
    }

    int fn(int i, int j, int f, int &k) {
        if(i > k + 1 || (f && i >= k + 1)) return 0;

        string key = to_string(i) + "$" + to_string(j) + "$" + to_string(f);
        if(dp.find(key) != dp.end()) return dp[key];

        int ans = 0;
        if(i == k) ans = 1;

        if(!f) ans += fn(i - 1, j, 1, k);
        ans += fn(i + (1 << j), j + 1, 0, k);
        return dp[key] = ans;
    }
};
