class Solution {
public:
    unordered_map<int, vector<vector<int>>> memo;
    int solve(int t, int p, int j, bool b) {
        int w = (p == t);
        if (memo.count(p) && memo[p][j][b] != -1) return memo[p][j][b];
        if ( j > 31 || p > t + 5 )  return 0;
        if (p + (1 << j) <= t + 1) w += solve(t, p + (1 << j), j + 1, true);
        if (b)  w += solve(t, p - 1, j, false);
        memo[p].resize(32, vector<int>(2, -1));
        return memo[p][j][b] = w;
    }
    int waysToReachStair(int t) {
        return solve(t, 1, 0, true);
    }
};
