class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def dp(stair, jump, prevDown):
            if stair > k+1:
                return 0
            if stair == k:
                ways = 1
            else:
                ways = 0
            if not prevDown:
                ways += dp(stair-1, jump, True)
            ways += dp(stair+2**jump, jump+1, False)
            return ways
        return dp(1, 0, False)
