class Solution:
    def waysToReachStair(self, k: int) -> int:
        res = 0
        max_jump = 31
        @cache
        def dfs(i, cur_jump, is_pre_minus):
            if cur_jump > 32 or i < 0:
                return 0
            if is_pre_minus:
                if i != k:
                    return dfs(i + 2 ** (cur_jump), cur_jump + 1, False)
                else:
                    return 1 + dfs(i + 2 ** (cur_jump), cur_jump + 1, False)
            else:
                if i != k:
                    return dfs(i - 1, cur_jump, True) + dfs(i + 2 ** (cur_jump), cur_jump + 1, False)
                else:
                    return 1 + dfs(i - 1, cur_jump, True) + dfs(i + 2 ** (cur_jump), cur_jump + 1, False)
        return dfs(1, 0, False)
