from functools import cache

class Solution:
    def waysToReachStair(self, k: int) -> int:

        @cache
        def dp(stair: int, jump: int, down_allowed: bool) -> int:
            if down_allowed:
                if stair > k + 1:
                    return 0
            else:
                if stair > k:
                    return 0
            count: int = 0
            if stair == k:
                count += 1
            if stair > 0 and down_allowed:
                count += dp(stair - 1, jump, False)
            count += dp(stair + 2 ** jump, jump + 1, True)
            return count

        return dp(1, 0, True)
