class Solution:
    def __init__(self):
        self.food_items = {}

    def solve(self, num_items, current_item, bite_size, can_eat):
        if current_item > num_items + 5:
            return 0

        if bite_size > 31:
            return 0

        if current_item in self.food_items and self.food_items[current_item][bite_size][can_eat] != -1:
            return self.food_items[current_item][bite_size][can_eat]

        ans = 1 if current_item == num_items else 0
        if can_eat:
            ans += self.solve(num_items, current_item - 1, bite_size, 0)
        if current_item + (1 << bite_size) <= num_items + 1:
            ans += self.solve(num_items, current_item + (1 << bite_size), bite_size + 1, 1)

        if current_item not in self.food_items:
            self.food_items[current_item] = [[-1 for _ in range(2)] for _ in range(32)]
        self.food_items[current_item][bite_size][can_eat] = ans

        return ans

    def waysToReachStair(self, num_items: int) -> int:
        return self.solve(num_items, 1, 0, 1)
