class Solution {
public:
    unordered_map<long long, unordered_map<bool, unordered_map<long long, long long>>>m;
    long long int s(long long k, bool pre, long long cur, long long j){
        if(cur > k + 1)return 0;
        if(cur < 0)return 0;
        long long res = 0;
        if(cur == k){
            res++;
        }
        if(m.count(cur) && m[cur].count(pre) && m[cur][pre].count(j))return m[cur][pre][j];
        if(!pre){
            res += s(k, true, cur - 1, j);
        }
        res += s(k, false, cur + pow(2, j), j + 1);
        return  m[cur][pre][j] = res;
    }
    int waysToReachStair(int k) {
        return s(k, false, 1, 0);
    }
};
