class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def dfs(i, jump, is_last_jump_down):
            res = int(i == k)  

            if i > k + 1:
                return res

            if i and not is_last_jump_down:
                res += dfs(i - 1, jump, True)

            res += dfs(i + pow(2, jump), jump + 1, False)

            return res

        return dfs(1, 0, False)
