import java.util.HashMap;
import java.util.Map;

public class Solution {
    private Map<String, Integer> dp;

    public Solution() {
        dp = new HashMap<>();
    }

    private int solve(int k, int i, int jump, boolean can) {
        if (i > k + 5) {
            return 0;
        }

        if (jump > 31) {
            return 0;
        }

        String key = i + "," + jump + "," + can;
        if (dp.containsKey(key)) {
            return dp.get(key);
        }

        int ans = (i == k) ? 1 : 0;
        if (can) {
            ans += solve(k, i - 1, jump, false);
        }
        if (i + (1 << jump) <= k + 1) {
            ans += solve(k, i + (1 << jump), jump + 1, true);
        }

        dp.put(key, ans);
        return ans;
    }

    public int waysToReachStair(int k) {
        dp.clear();
        return solve(k, 1, 0, true);
    }
}
