#define ll long long
class Solution {
    ll kp;
    unordered_map<int, vector<vector<int>>> dp;
    int rec( int level, int jump, bool valid) {
        if (level > kp + 6)
            return 0;

        if(jump > 32)
            return 0;

        if(dp.count(level) && dp[level][jump][valid] != -1)
            return dp[level][jump][valid];

        int ans = 0;
        if(level==kp)ans=1;
        if (valid)
            ans += rec( level - 1, jump, 0);
        if (level + (1 << jump) <= kp + 1)
            ans += rec( level + (1 << jump), jump + 1, 1);

        dp[level].resize(33, vector<int>(2, -1));

        return dp[level][jump][valid] = ans;
    }
public:
    int waysToReachStair(int k) {
        kp=k;
        ll ans=rec(1,0,1);
        return ans;
    } 
};
