#define f first
#define s second
#define pb push_back
#define all(x) x.begin(),x.end()
#define sz(x) (long long)(x).size()
#define pii pair<long long,long long>

long long glob=0;
const long long MAXN = 1e5;
const long long MOD = (1<<61)-1;

long long fac[MAXN + 1];
long long inv[MAXN + 1];

long long exp(long long x, long long n, long long m) {
	x %= m;
	long long res = 1;
	while (n > 0) {
		if (n % 2 == 1) { res = res * x % m; }
		x = x * x % m;
		n /= 2;
	}
	return res;
}

void factorial() {
	fac[0] = 1;
	for (long long i = 1; i <= MAXN; i++) { fac[i] = fac[i - 1] * i % MOD; }
}

void inverses() {
	inv[MAXN] = exp(fac[MAXN], MOD - 2, MOD);
	for (long long i = MAXN; i >= 1; i--) { inv[i - 1] = inv[i] * i % MOD; }
}

long long choose(long long n, long long r) { 
	if(r>n)return 0ll;
	return (fac[n] * inv[r] % MOD * inv[n - r] % MOD)%MOD; 
}

class Solution {
public:
    int waysToReachStair(int k) {
    	if(glob==0){
    		factorial();
    		inverses();
    		glob++;
    	}
    	int ans=0;
  		for(long long use=0;use<60;use++){
  			long long rem=(1<<use)-1-k+1;
  			if(rem>use+1)
  				break;
  			if(rem<0)
  				continue;
  			ans+=choose(use+1,rem);
  		}      
  		return ans;
    }
};
