class Solution {
private:
    map<int, map<int, map<int, int>>> mp;
    long long ways(int k, int p, long long num, bool turn) {
        int ans = (num == k);;
        if (p >= 32 && num > 2 * k) return 0;
        if (mp.find(num) != mp.end()) {
            if (mp[num].find(p) != mp[num].end()) {
                if (mp[num][p].find(turn) != mp[num][p].end()) {
                    return mp[num][p][turn];
                }
            }
        }
        if (turn && num > 0) ans += ways(k, p, num - 1, false);
        if (k + 1 >= num + pow(2, p)) ans += ways(k, p + 1, num + power(2, p), true);
        return mp[num][p][turn] = ans;
    }
    long long power(long long a, long long b) {
        long long ans = 1;
        while (b) {
            if (b & 1) ans = ans * a;
            a = a * a;
            b >>= 1;
        }
        return ans;
    }
public:
    int waysToReachStair(int k) {
        return ways(k, 0, 1, true);
    }
};
