    int func(int n,int r){
        long long sum = 1;
        for(int i = 1; i <= r; i++){
            sum = sum * (n - r + i) / i;
        }
        return sum;
    }

    int waysToReachStair(int k) {
        int res = 0;

        int n = k;

        int sum=1,n1=1,j=0;
        while(1){
            if(sum<k){
                sum+= (1<<j);
                j++;
                n1++;
            }
            else{
                if(sum==k){
                    res++;
                    sum+= (1<<j);
                    j++;
                    n1++;
                }
                else{
                    if((sum-n1)<=k){
                        // n1 C (sum-k)
                        res+= func(n1,sum-k);
                        sum+=(1<<j);
                        n1++;
                        j++;

                    }
                    else{
                        break;
                    }
                }
            }
        }
        return res;
    }
};
