public class Solution {
    public int WaysToReachStair(int k) 
    {
        if (k == 0) return 2;
        if (k == 1) return 4;
        if (k == 2) return 4;
        if (k == 3) return 3;
        if (k == 4) return 2;

        //long[] row = new long[] { 1, 2, 3, 4 };
        long row = 1;
        long[] cnt = new long[] { 1, 3, 3, 1 };

        for (int p = 4, i = 5; row <= k; p <<= 1) 
        {
            row += p - 1;
            cnt = cnt.Append(0).Zip(cnt.Prepend(0), (f, s) => f + s).ToArray();

            if (row <= k && row + i - 1 >= k) return (int)cnt[k - row];
            ++i;
        }
        return 0;
    }
}
