class Solution {
    using ll = long long;
public:
    ll get_dp(int n, int k) {
        if (k == 0) return 1;
        if (k > ((n+1)/2))return 0;


        ll dp[n][2][k+1];
        for(ll i=0;i<n;i++)for(ll j=0;j<2;j++)for(ll l=0;l<=k;l++) dp[i][j][l]=0;

        dp[0][0][0] = 1;
        dp[0][1][1] = 1;

        for (ll i = 0; i < n; i++) {
            for (ll j = 0; j < 2; j++) {
                dp[i][j][0] = (j==0);
            }
        }

        for (ll p = 1; p < n; p++) {
            for (ll q = 0; q < 2; q++) {
                for (ll r = 1; r <= k; r++) {
                    if (q==0){
                        dp[p][q][r] += dp[p-1][0][r];
                        dp[p][q][r] += dp[p-1][1][r];
                    }
                    if (q==1) {
                        dp[p][q][r] += dp[p-1][0][r-1];
                    }
                }
            }
        }

        ll fans=0;
        fans += dp[n-1][0][k];
        fans += dp[n-1][1][k];
        return fans;
    }
    int waysToReachStair(int k) {
        ll res = 0;
        for (ll bj = 0; bj <= 32; bj++) {
            for (ll fj = 0; fj <= 32; fj++) {
                if ((1ll<<fj)-bj==k){
                    res += get_dp(bj+fj,bj);
                }
            }
        }

        return (int)res;
    }
};
