class Solution {
public:
    map<pair<int,pair<int,int>>,int> dp;
    int tg;

    int f(int i, int jump, int minus) {
        int op = 0;
        if(i==tg) op++;
        if(i > tg + 1) {
            return 0;
        }
        if(i < 0) {
            return 0;
        }
        if(jump > 31) {
            return 0;
        }
        if(dp.count({i,{jump,minus}})) {
            return dp[{i,{jump,minus}}];
        }
        op += f(i + (1 << jump), jump + 1, 0);
        if(minus == 0 ) {
            op += f(i - 1, jump, 1);
        }
        return dp[{i,{jump,minus}}] = op;
    }

    int waysToReachStair(int k) {
        tg = k;
        return f(1, 0, 0);
    }
};
