class Solution:
    def waysToReachStair(self, k: int) -> int:
        mem = {}
        def no_of_ways(pos,jump,lastop):
            if pos < 0:
                return 0
            if (pos,jump,lastop) in mem:
                return mem[(pos,jump,lastop)]
            add = 0
            if pos == k:
                add = 1
            if lastop:
                if pos >= k and jump > 0:
                    mem[(pos,jump,lastop)] = no_of_ways(pos-1,jump,False) + add
                else:
                    mem[(pos,jump,lastop)] = no_of_ways(pos-1,jump,False) + no_of_ways(pos+(2**jump),jump+1,True) + add
            else:
                if pos >= k and jump > 0:
                    mem[(pos,jump,lastop)] = add
                else:
                    mem[(pos,jump,lastop)] = add + no_of_ways(pos+(2**jump),jump+1,True)
            return mem[(pos,jump,lastop)]
        return no_of_ways(1,0,True)
