#define F first
#define S second
const int mod=1e9+7;
long long inv(int a) {
  return a <= 1 ? a : mod - (long long)(mod/a) * inv(mod % a) % mod;
}
class Solution {
public:
    int waysToReachStair(int k) {
        vector<long long> v(50,1ll);
        for(int i=1;i<50;i++){
            v[i]=(v[i-1]*i)%mod;
        }

        if(k==0){
            return 2;

        }
        int ans=0;
        for(int i=1;i<=30;i++){
            long long b=(1ll<<i);
            if(b-k<=i+1 && b>=k){
                int f=b-k;
                long long temp=v[i+1];
                temp*=inv(v[b-k])%mod;
                temp%=mod;
                temp*=inv(v[i+1-b+k])%mod;
                temp%=mod;
                ans+=temp;
                cout<<i<<' '<<temp<<endl;


            }else {
                // break;

            }

        }
        cout<<endl;
        if(k==1){
            return ans+1;
        }
        return ans;



    }
};
