class Solution:
    def jump(self, down: bool, cur, jump: int) -> int:
        # print(down, cur, jump)
        if (down, cur, jump) in self.result:
            return self.result[(down,cur,jump)]
        res = 0
        if cur-1 > self.k:
            self.result[(down,cur,jump)] = 0
            return 0
        if cur == self.k:
            res += 1
        if down:
            res += self.jump(False, cur-1, jump)
        res += self.jump(True, cur+self.m[jump], jump+1)
        self.result[(down,cur,jump)] = res
        return res
    def waysToReachStair(self, k: int) -> int:
        self.k = k
        self.m = []
        self.result = dict()
        for i in range(32):
            self.m.append(2**i)
        return self.jump(True, 1, 0)
