class Solution {
    int atul(int i, int k, int jmp, bool ms,unordered_map<int, vector<vector<int>>>& dp) {
        if(i > k + 5||jmp > 32)
            return 0;
        if(dp.count(i) && dp[i][jmp][ms] != -1)
            return dp[i][jmp][ms];
        int ans = 0;
        if(k==i)ans++;
        if (ms==1)
            ans += atul(i-1, k, jmp, 0,dp);
        if (i + (1 << jmp) <= k + 1)
            ans += atul(i + (1 << jmp), k, jmp + 1, 1,dp);

        dp[i].resize(33, vector<int>(2, -1));

        return dp[i][jmp][ms] = ans;
    }
public:
    int waysToReachStair(int k) {
        unordered_map<int, vector<vector<int>>> dp;
        return atul(1,k,0,1,dp);
    } 
};
