class Solution:
    def waysToReachStair(self, k: int) -> int:

        @cache
        def traverse(idx, jump, canGoBack):
            result = 0
            if idx == k:
                result = 1

            op1 = 0
            if canGoBack and idx != 0:
                op1 = traverse(idx-1, jump, False)

            op2 = 0
            if idx + pow(2, jump) <= k+1:
                op2 = traverse(idx + pow(2, jump), jump+1, True)

            return op1 + op2 + result

        return traverse(1, 0, True)
