class Solution {
public:
    int solve(int k, int jump, bool alt, map<pair<int, pair<int,bool>>, int> &dp){
        if(k<0){
            return 0;
        }
        if(dp.find({k,{jump,alt}}) != dp.end()){
            return dp[{k,{jump,alt}}];
        }
        if(k==1 && jump > 1){
            return 1;
        }
        int numWays = k == 1;
        if(alt){
           numWays += solve(k+1, jump, false, dp); 
        }
        numWays += solve(k-(1<<jump), jump+1, true, dp);
        return dp[{k, {jump, alt}}] = numWays;
    }
    int waysToReachStair(int k) {
        map<pair<int, pair<int,bool>>, int> dp;
        return solve(k, 0, true, dp);
    }
};
