class Solution {
public:

    int solve(int k,int i,int jump,bool f,unordered_map<int,unordered_map<int,unordered_map<bool,int>>>&dp){
        if(i<0 || i>k+1)return 0;
        int i1=0,i2=0;
        if(dp[i][jump].find(f)!=dp[i][jump].end())return dp[i][jump][f];
        if(i==k){
           if(f)i1=solve(k,i-1,jump,0,dp);
           i2=solve(k,i+(1<<jump),jump+1,1,dp);
            return dp[i][jump][f]=i1+i2+1;
        }
        if(f) i1=solve(k,i-1,jump,0,dp);
         i2=solve(k,i+(1<<jump),jump+1,1,dp);
        return dp[i][jump][f]=i1+i2;
    }
    int waysToReachStair(int k) {
        unordered_map<int,unordered_map<int,unordered_map<bool,int>>>dp;
        return solve(k,1,0,1,dp);
    }
};
